import React from 'react';
import { Box, Container, Grid, Typography, Card, CardContent } from '@mui/material';
import { SportsTennis, Event, School, People } from '@mui/icons-material';


const Features = () => {
  const features = [
    {
      icon: <Trophy className="w-8 h-8" />,
      title: 'Metodologia Exclusiva',
      description: 'Sistema de ensino desenvolvido por profissionais com experiência internacional, adaptado para cada faixa etária.',
      color: 'from-blue-500 to-blue-600',
    },
    {
      icon: <Calendar className="w-8 h-8" />,
      title: 'Turmas Especializadas',
      description: 'Grupos divididos por idade e nível técnico, garantindo evolução constante e aprendizado adequado.',
      color: 'from-purple-500 to-purple-600',
    },
    {
      icon: <GraduationCap className="w-8 h-8" />,
      title: 'Professores Certificados',
      description: 'Equipe técnica formada por profissionais certificados CBT com vasta experiência no ensino do tênis.',
      color: 'from-orange-500 to-orange-600',
    },
    {
      icon: <Users className="w-8 h-8" />,
      title: 'Acompanhamento Individual',
      description: 'Cada aluno recebe atenção personalizada com relatórios de evolução e metas específicas.',
      color: 'from-green-500 to-green-600',
    },
    {
      icon: <Clock className="w-8 h-8" />,
      title: 'Horários Flexíveis',
      description: 'Aulas nos períodos manhã, tarde e noite, adaptando-se à rotina escolar e profissional.',
      color: 'from-red-500 to-red-600',
    },
    {
      icon: <MapPin className="w-8 h-8" />,
      title: 'Estrutura Completa',
      description: '12 quadras profissionais, vestiários modernos, lanchonete e área de descanso para pais e alunos.',
      color: 'from-teal-500 to-teal-600',
    },
  ];

  return (
    <section id="features" className="py-20 bg-gradient-to-br from-gray-50 to-white">
      <div className="container mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="text-center mb-16">
          <h2 className="text-4xl sm:text-5xl font-bold text-gray-900 mb-4">
            Por que escolher nossa
            <span className="bg-gradient-to-r from-green-600 to-green-400 bg-clip-text text-transparent">
              {" "}Escola de Tênis?
            </span>
          </h2>
          <p className="text-xl text-gray-600 max-w-3xl mx-auto">
            Nossa metodologia exclusiva e estrutura completa garantem o melhor desenvolvimento
            técnico e tático para alunos de todas as idades
          </p>
        </div>

        {/* Features Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {features.map((feature, index) => (
            <div
              key={index}
              className="group relative bg-white rounded-2xl p-8 shadow-lg hover:shadow-2xl transition-all duration-300 transform hover:-translate-y-2 border border-gray-100"
            >
              {/* Icon */}
              <div className={`inline-flex items-center justify-center w-16 h-16 rounded-xl bg-gradient-to-r ${feature.color} text-white mb-6 group-hover:scale-110 transition-transform duration-300`}>
                {feature.icon}
              </div>

              {/* Content */}
              <h3 className="text-xl font-bold text-gray-900 mb-3 group-hover:text-green-600 transition-colors">
                {feature.title}
              </h3>
              <p className="text-gray-600 leading-relaxed">
                {feature.description}
              </p>

              {/* Hover Effect */}
              <div className="absolute inset-0 bg-gradient-to-r from-green-500/5 to-green-600/5 rounded-2xl opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>
            </div>
          ))}
        </div>

        {/* CTA Section */}
        <div className="text-center mt-16">
          <div className="bg-gradient-to-r from-green-600 to-green-700 rounded-2xl p-8 sm:p-12 text-white">
            <h3 className="text-3xl font-bold mb-4">
              Pronto para elevar seu jogo?
            </h3>
            <p className="text-xl text-green-100 mb-8 max-w-2xl mx-auto">
              Agende uma visita gratuita e conheça nossas instalações de primeira classe
            </p>
            <button className="bg-white text-green-600 px-8 py-4 rounded-xl font-semibold text-lg hover:bg-green-50 transition-colors transform hover:scale-105 duration-300">
              Agendar Visita Gratuita
            </button>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Features;
